import logo from './logo.svg';
import React, { useState } from "react";
import "./style.css";

const emojiDictionary = {
  "üôÇ": "Acha",
  "üòç": "wow",
  "ü§≠": "hehehe",
  "üòè": "Achaaaw"
};

function App() {
  const [meaning, setMeaning] = useState("");

  function emojiChangeHandler(event) {
    var meaning = emojiDictionary[event.target.value];
    if (meaning === undefined) {
      meaning = "We don't have this in the database";
    }

    setMeaning(meaning);
  }

  function emojiClickHandler(emoji) {
    var meaning = emojiDictionary[emoji];
    setMeaning(meaning);
  }

  return (
    <div className="App" style={{ fontFamily: "Arial, sans-serif", textAlign: "center", paddingTop: "2rem" }}>
      <h1 style={{ color: "#4CAF50" }}>Emoji Interpreter</h1>
      <input
        onChange={emojiChangeHandler}
        placeholder="Enter an emoji"
        style={{ padding: "0.5rem", fontSize: "1rem", marginBottom: "1rem" }}
      />

      <h3 style={{ color: "#1E88E5" }}>{meaning}</h3>
      <h3 style={{ color: "#673AB7" }}>Emojis we know</h3>
      <div>
        {Object.keys(emojiDictionary).map(function (emoji) {
          return (
            <span
              onClick={() => emojiClickHandler(emoji)}
              style={{
                padding: "0.5rem",
                fontSize: "2rem",
                cursor: "pointer",
                margin: "0.5rem",
                color: "#FFC107",
              }}
              key={emoji}
            >
              {emoji}
            </span>
          );
        })}
      </div>
    </div>
  );
}

export default App;
